import { extendTheme } from "@chakra-ui/react";

export default extendTheme({
  colors: {
    primary: "#0ad4fa",
    secondary: "#FF7F01",
  },
  fonts: {
    body: "SourceSansPro-Regular, system-ui, sans-serif",
    heading: "SourceSansPro-Bold",
    mono: "SourceSansPro-Bold",
  },
  shadows: {
    outline: "0",
  },
});
